export function dijkstra(grid, startNode, endNode) {
  const visitedNodes = [];
  startNode.distance = 0;
  const unvisitedNodes = getAllNodes(grid);
  while (unvisitedNodes.length > 0) {
    //u is the node with shortest distance in univistednodes
    //remove closestNode from unvisited nodes
    const closestNeighbor = getClosestNode(unvistedNodes);
    //for each neighbor v of u
    //alt = distance u + distance (v-u)
    //if  alt < dist[v]
    //dist[v] = alt
    //previous node = u
  }
}

function getClosestNode(univistedNodes) {
  //arrange in ascending order by distance
  unvisitedNodes.sort((A, B) => {
    A.distance - B.distance;
  });
  //remove closestNode from unvisited nodes
  const node = univistedNodes.shift();
  return node;
}

function getAllNodes(grid) {
  const nodes = [];
  for (const column of grid) {
    for (const node of column) {
      nodes.push(node);
    }
  }
  return nodes;
}
