import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor() {
    super();
    this.state = {
      isMouseUp: true,
      isMouseDown: false,
      didMouseEnter: false,
      didMouseMove: false,
      didMouseLeave: false,
    };
  }
  onMouseDown = () => {
    this.setState({ isMouseDown: true, onMouseUp: false });
  };
  onMouseUp = () => {
    this.setState({ isMouseUp: true, onMouseDown: false });
    console.log(
      this.state.isMouseDown,
      this.state.isMouseUp,
      this.state.didMouseEnter,
      this.state.didMouseLeave,
      this.state.didMouseMove
    );
  };
  onMouseMove = () => {
    this.setState({ isMouseDown: true });
  };
  onMouseEnter = () => {
    this.setState({ didMouseEnter: true });
  };
  onMouseLeave = () => {
    this.setState({ didMouseEnter: false });
  };
  render() {
    const { column, row, isStart, isFinish, isWall } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onMouseDown={this.onMouseDown}
        onMouseUp={this.onMouseUp}
        onMouseEnter={this.onMouseEnter}
        onMouseMove={this.onMouseMove}
        onMouseLeave={this.onMouseLeave}
      />
    );
  }
}
export default Node;
