import React, { Component } from "react";
import Node from "./Node.js";
import "./PathFinder.css";
import { dijkstra, getShortestPath } from "../algorithms/dijkstra";
import {
  START_NODE_COL,
  START_NODE_ROW,
  END_NODE_COL,
  END_NODE_ROW,
} from "../constants";

const freeze = true;
const initialState = {
  grid: [],
};

class PathFinder extends Component {
  constructor() {
    super();
    this.state = initialState;
  }
  componentDidMount() {
    const grid = drawInitialGrid();
    this.setState({ grid: grid });
  }
  visualizeAlgorithm() {
    const grid = this.state.grid;
    const startNode = grid[START_NODE_COL][START_NODE_ROW];
    const endNode = grid[END_NODE_COL][END_NODE_ROW];
    const visitedNodes = dijkstra(grid, startNode, endNode);
    const shortestPath = getShortestPath(startNode, endNode);
    this.removeCSS(visitedNodes, startNode, endNode);
    this.animateAlgorithm(visitedNodes, startNode, endNode, shortestPath);
  }

  //remove the CSS of the nodes after the first use of visualize button
  removeCSS(visitedNodes, startNode, endNode) {
    for (let i = 0; i < visitedNodes.length; i++) {
      const node = visitedNodes[i];
      if (node !== startNode && node !== endNode) {
        document.getElementById(`node-${node.column}-${node.row}`).className =
          "node";
      }
    }
  }
  animateAlgorithm(visitedNodes, startNode, endNode, shortestPath) {
    for (let i = 0; i < visitedNodes.length; i++) {
      if (i === visitedNodes.length - 1) {
        setTimeout(() => {
          this.displayShortestPath(shortestPath, startNode, endNode);
        }, 100 * i);
        return;
      }
      setTimeout(() => {
        const node = visitedNodes[i];
        if (node !== startNode && node !== endNode) {
          document.getElementById(`node-${node.column}-${node.row}`).className =
            "node-Visited";
        }
      }, 100 * i);
    }
  }
  displayShortestPath(shortestPath, startNode, endNode) {
    for (let i = 0; i < shortestPath.length; i++) {
      const node = shortestPath[i];
      if (node !== startNode && node !== endNode) {
        document.getElementById(`node-${node.column}-${node.row}`).className =
          "node-Path";
      }
    }
  }

  resetCSS() {
    const grid = [];
    while (document.querySelector(`.node-Visited`) != null) {
      document.querySelector(`.node-Visited`).className = "node";
    }
    while (document.querySelector(`.node-Path`) != null) {
      document.querySelector(`.node-Path`).className = "node";
    }
  }

  render() {
    const { grid } = this.state;
    return (
      <div className="container">
        <button id="start" onClick={() => this.visualizeAlgorithm()}>
          Visualize!
        </button>
        <button id="reset" onClick={() => this.resetCSS()}>
          Reset!
        </button>
        <div className="grid">
          {grid.map((row, rowIndex) => {
            return (
              <div key={rowIndex}>
                {row.map((coln, colnIndex) => {
                  const { row, column, isStart, isFinish, isWall } = coln;
                  return (
                    <Node
                      key={colnIndex}
                      row={row}
                      column={column}
                      isFinish={isFinish}
                      isStart={isStart}
                      isWall={isWall}
                    />
                  );
                })}
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
const drawInitialGrid = () => {
  const grid = [];
  for (let column = 0; column < 50; column++) {
    let currentColumn = [];
    for (let row = 0; row < 20; row++) {
      currentColumn.push(createNewNode(row, column));
    }
    grid.push(currentColumn);
  }
  return grid;
};
const createNewNode = (row, column) => {
  return {
    column,
    row,
    distance: Infinity,
    previousNode: null,
    isVisited: false,
    isWall: false,
    isStart: column === START_NODE_COL && row === START_NODE_ROW,
    isFinish: column === END_NODE_COL && row === END_NODE_ROW,
  };
};

export default PathFinder;
