import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  drag = event => {
    event.dataTransfer.setData("nodestart", event.target.id);
    this.className = "node";
  };
  drop = event => {
    event.preventDefault();
    let data = event.dataTransfer.getData("nodestart");
    let nodestart = document.getElementById(data);
    event.target.appendChild(nodestart);
    this.className = "nodefinish";
  };
  allowDrop = event => {
    event.preventDefault();
  };
  render() {
    const { column, row, isStart, isFinish, isWall } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        draggable="true"
        onDragStart={this.drag}
        onDrop={this.drop}
        onDragOver={this.allowDrop}
      />
    );
  }
}
export default Node;
