import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor() {
    super();
    this.state = {
      isMouseUp: true,
      isMouseDown: false,
      didMouseEnter: false,
      didMouseMove: false,
      didMouseLeave: false,
    };
  }
  onMouseDown = () => {
    this.setState({ isMouseDown: true, isMouseUp: false });
  };
  onMouseUp = () => {
    this.setState({ isMouseUp: true, isMouseDown: false });
  };
  onMouseMove = () => {
    this.setState({ didMouseMove: true });
    const {
      isMouseDown,
      isMouseUp,
      didMouseEnter,
      didMouseLeave,
      didMouseMove,
    } = this.state;
    if (
      isMouseDown === true &&
      isMouseUp === false &&
      didMouseEnter === true &&
      didMouseMove === true
    ) {
      console.log("start" + didMouseEnter + "  " + didMouseLeave);
    }
  };
  onMouseEnter = () => {
    this.setState({ didMouseEnter: true });
  };
  onMouseLeave = () => {
    this.setState({ didMouseLeave: true, didMouseEnter: false });
  };
  render() {
    const {
      column,
      row,
      isStart,
      isFinish,
      isWall,
      changeStartEndNodes,
    } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onMouseDown={this.onMouseDown}
        onMouseUp={this.onMouseUp}
        onMouseEnter={this.onMouseEnter}
        onMouseMove={this.onMouseMove}
        onMouseLeave={this.onMouseLeave}
        onClick={(e) => {
          changeStartEndNodes(e, this);
        }}
      />
    );
  }
}
export default Node;
