import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor() {
    super();
    this.state = {
      isMouseUp: true,
      isMouseDown: false,
      didMouseEnter: false,
      didMouseMove: false,
      didMouseLeave: false,
    };
  }
  onMouseDown = (e, node) => {
    console.log(e.target.className);
    this.setState({ isMouseDown: true, isMouseUp: false });
  };
  onMouseUp = (e, node) => {
    console.log(e.target.className);
    this.setState({ isMouseUp: true, isMouseDown: false });
  };
  onMouseMove = () => {
    this.setState({ didMouseMove: true });
  };
  onMouseEnter = () => {
    this.setState({ didMouseEnter: true });
  };
  onMouseLeave = () => {
    this.setState({ didMouseLeave: true, didMouseEnter: false });
  };
  onDragStart = (e) => {
    console.log("drag start");
    e.dataTransfer.setData("text", e.target.className);
    console.log(e.target.className);
  };
  onDrop = (e, node) => {
    console.log("drop");
    e.preventDefault();
    let data = e.dataTransfer.getData("text");
    document.querySelector(`.nodestart`).className = "node";
    document.getElementById(
      `node-${node.props.column}-${node.props.row}`
    ).className = "nodestart";
    e.target.appendChild(document.querySelector(".node"));
    console.log(e.target.className);
  };
  onDragOver = (e) => {
    console.log("drag over");
    e.preventDefault();
  };
  render() {
    const {
      column,
      row,
      isStart,
      isFinish,
      isWall,
      changeStartEndNodes,
    } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        draggable="true"
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onDragStart={(e) => this.onDragStart(e)}
        onDragOver={(e) => this.onDragOver(e)}
        onDrop={(e) => this.onDrop(e, this)}
        // onMouseDown={(e) => this.onMouseDown(e, this)}
        // onMouseUp={(e) => this.onMouseUp(e, this)}
        // onMouseEnter={this.onMouseEnter}
        // onMouseMove={this.onMouseMove}
        // onMouseLeave={this.onMouseLeave}
        // onClick={(e) => {
        //   changeStartEndNodes(e, this);
        // }}
      />
    );
  }
}
export default Node;
