import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor() {
    super();
    this.state = {
      isMousedown: false,
      didMouseEnter: false,
      isMouseUp: true
    };
  }
  onMouseDown = event => {
    if (event.type === "mousedown") {
      this.setState({ isMouseDown: true, isMouseUp: false });
    }
    console.log(this.state.isMousedown);
  };
  onMouseEnter = event => {
    console.log(this.state.isMousedown);
  };
  render() {
    const { column, row, isStart, isFinish, isWall } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onMouseDown={this.onMouseDown}
        onMouseEnter={this.onMouseEnter}
      />
    );
  }
}
export default Node;
