export function dijkstra(grid, startNode, endNode) {
  const visitedNodes = [];
  startNode.distance = 0;
  const unvisitedNodes = getAllNodes(grid);
  //   while (unvisitedNodes.length > 0) {
  //   start at the start node and get the closest neighbor
  const closestNode = getClosestNode(unvisitedNodes);
  console.log(closestNode);
  //   }
}
function getClosestNode(unvisitedNodes) {
  //arrange in ascending order by distance
  unvisitedNodes.sort((A, B) => A.distance - B.distance);
  //remove closestNode from unvisited nodes
  const node = unvisitedNodes.shift();
  return node;
}
function getAllNodes(grid) {
  const nodes = [];
  for (const column of grid) {
    for (const node of column) {
      nodes.push(node);
    }
  }
  return nodes;
}
