import React, { Component } from "react";
import Node from "./Node.js";
import "./PathFinder.css";

class PathFinder extends Component {
  constructor() {
    super();
    this.state = {
      grid: []
    };
  }
  componentDidMount() {
    const grid = drawInitialGird();
    this.setState({ grid: grid });
  }
  render() {
    const { grid } = this.state;
    return (
      <div className="grid">
        {grid.map((row, rowIndex) => {
          return (
            <div key={rowIndex}>
              {row.map((coln, colnIndex) => {
                const { row, column, isStart, isFinish, isWall } = coln;
                return (
                  <Node
                    key={colnIndex}
                    row={row}
                    column={column}
                    isFinish={isFinish}
                    isStart={isStart}
                    isWall={isWall}
                  />
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}
const drawInitialGird = () => {
  const grid = [];
  for (let column = 0; column < 50; column++) {
    let currentColumn = [];
    for (let row = 0; row < 20; row++) {
      currentColumn.push(createNewNode(row, column));
    }
    grid.push(currentColumn);
  }
  console.log(grid);
  return grid;
};
const createNewNode = (row, column) => {
  console.log(column, row);
  return {
    column,
    row,
    isStart: row === 5 && column === 0,
    isFinish: row === 9 && column === 0,
    distance: Infinity,
    isVisited: false,
    isWall: false,
    previousNode: null
  };
};

export default PathFinder;
