import React, { Component } from "react";
import Node from "./Node.js";
import "./PathFinder.css";

class PathFinder extends Component {
  constructor() {
    super();
    this.state = {
      grid: []
    };
  }
  componentDidMount() {
    const grid = drawInitialGird();
    this.setState({ grid: grid });
  }
  render() {
    const { grid } = this.state;
    return (
      <div className="grid">
        {grid.map((row, rowIndex) => {
          return (
            <div key={rowIndex}>
              {row.map((coln, colnIndex) => {
                const { row, column, isFinish, isWall } = coln;
                return <Node key={colnIndex} row={row} column={column}></Node>;
              })}
            </div>
          );
        })}
        <Node></Node>
      </div>
    );
  }
}
const drawInitialGird = () => {
  const grid = [];
  for (let row = 0; row < 30; row++) {
    let currentRow = [];
    for (let column = 0; column < 30; column++) {
      currentRow.push(createNewNode(column, row));
    }
    grid.push(currentRow);
  }
  return grid;
};
const createNewNode = (column, row) => {
  return {
    column,
    row,
    isStart: row === 15 && column === 5,
    isFinish: row === 15 && column === 25,
    distance: Infinity,
    isVisited: false,
    isWall: false,
    previousNode: null
  };
};

export default PathFinder;
