import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor(props) {
    super();
    this.state = {
      isMouseUp: true,
      isMouseDown: false,
      didMouseEnter: false,
      didMouseMove: false,
      didMouseLeave: false,
    };
  }
  onMouseDown = () => {
    this.setState({ isMouseDown: true, isMouseUp: false });
  };
  onMouseUp = () => {
    this.setState({ isMouseUp: true, isMouseDown: false });
  };
  onMouseMove = () => {
    this.setState({ didMouseMove: true });
    // console.log(
    //   "up " +
    //     this.state.isMouseUp +
    //     "  down  " +
    //     this.state.isMouseDown +
    //     "  enter  " +
    //     this.state.didMouseEnter +
    //     "  leave " +
    //     this.state.didMouseLeave +
    //     "  move " +
    //     this.state.didMouseMove
    // );
  };
  onMouseEnter = () => {
    this.setState({ didMouseEnter: true, didMouseLeave: false });
  };
  onMouseLeave = () => {
    this.setState({ didMouseLeave: true, didMouseEnter: false });
  };
  getName() {
    this.props.changeStartEndNodes(this);
  }
  render() {
    const {
      column,
      row,
      isStart,
      isFinish,
      isWall,
      changeStartEndNodes,
    } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onMouseDown={this.onMouseDown}
        onMouseUp={this.onMouseUp}
        onMouseEnter={this.onMouseEnter}
        onMouseMove={this.onMouseMove}
        onMouseLeave={this.onMouseLeave}
        onClick={this.getName}
      />
    );
  }
}
export default Node;
