import React, { Component } from "react";
import "./Node.css";

class Node extends Component {
  constructor() {
    super();
    this.state = {
      isMouseUp: true,
      isMouseDown: false,
      didMouseEnter: false,
      didMouseMove: false,
      didMouseLeave: false,
    };
  }
  onMouseDown = (e, node) => {
    console.log(e.target.className);
    this.setState({ isMouseDown: true, isMouseUp: false });
  };
  onMouseUp = (e, node) => {
    console.log(e.target.className);
    this.setState({ isMouseUp: true, isMouseDown: false });
  };
  onMouseMove = () => {
    this.setState({ didMouseMove: true });
    const {
      isMouseDown,
      isMouseUp,
      didMouseEnter,
      didMouseLeave,
      didMouseMove,
    } = this.state;
    if (
      isMouseDown === true &&
      isMouseUp === false &&
      didMouseEnter === true &&
      didMouseMove === true &&
      didMouseLeave === false
    ) {
      console.log("start  " + didMouseEnter + "  " + didMouseLeave);
    }
  };
  onMouseEnter = () => {
    this.setState({ didMouseEnter: true });
  };
  onMouseLeave = () => {
    this.setState({ didMouseLeave: true, didMouseEnter: false });
  };
  onDragStart = (e) => {
    console.log("drag start");
    e.dataTransfer.setData("text", e.target.className);
    console.log(e.target.className);
  };
  onDrop = (e) => {
    console.log("drop");
    e.preventDefault();
    let data = e.dataTransfer.getData("text");
    e.target.appendChild(document.querySelector(data));
    console.log(e.target.className);
  };
  onDragOver = (e) => {
    console.log("drag over");
    e.preventDefault();
  };
  render() {
    const {
      column,
      row,
      isStart,
      isFinish,
      isWall,
      changeStartEndNodes,
    } = this.props;
    const extraClassName = isFinish
      ? "finish"
      : isStart
      ? "start"
      : isWall
      ? "node-wall"
      : "";
    return (
      <div
        draggable="true"
        id={`node-${column}-${row}`}
        className={`node${extraClassName}`}
        onDragStart={(e) => this.onDragStart}
        onDragOver={(e) => this.onDragOver(e)}
        onDrop={(e) => this.onDrop}
        // onMouseDown={(e) => this.onMouseDown(e, this)}
        // onMouseUp={(e) => this.onMouseUp(e, this)}
        // onMouseEnter={this.onMouseEnter}
        // onMouseMove={this.onMouseMove}
        // onMouseLeave={this.onMouseLeave}
        // onClick={(e) => {
        //   changeStartEndNodes(e, this);
        // }}
      />
    );
  }
}
export default Node;
